def get_app_stylesheet():    return """    /* ===================== MAIN ===================== */    /* ===================== RIGHT PANEL ===================== */    #rightPanel {        background: #ffffff;   /* açık gri */    }    QWidget {        background: #ffffff;    }        #leftPanel {    background: #ffffff;   /* Nature-style açık gri */    }    /* ===================== GROUP BOX ===================== */    QGroupBox {        background: #ffffff;        border: 1px solid #e5e7eb;        border-radius: 12px;        margin-top: 10px;        padding-top: 10px;        font-weight: 600;    }    QGroupBox::title {        subcontrol-origin: margin;        left: 12px;        padding: 0 6px;        background: #ffffff;        color: #111827;    }    /* ===================== TEXT ===================== */    QLabel {        color: #111827;        font-size: 11px;        font-weight: 600;    }    QLineEdit, QComboBox, QListWidget {        background: #ffffff;        border: 1px solid #d1d5db;        border-radius: 8px;        padding: 6px 8px;        font-size: 11px;        min-height: 24px;    }    QLineEdit:focus,    QComboBox:focus,    QListWidget:focus {        border: 2px solid #2563eb;    }    QLineEdit:read-only {        background: #f3f4f6;        color: #6b7280;    }    /* ===================== COMBO POPUP ===================== */    QComboBox QAbstractItemView {        background: #ffffff;        border: 1px solid #d1d5db;        selection-background-color: #2563eb;        selection-color: #ffffff;    }    /* ===================== BUTTONS ===================== */    QPushButton {        border: none;        border-radius: 10px;        padding: 8px 12px;        font-size: 11px;        font-weight: 700;        min-height: 34px;    }    QPushButton#DataButton {        background: #2563eb;        color: white;    }    QPushButton#purpleButton {        background: #7c3aed;        color: white;    }    QPushButton#greenButton {        background: #16a34a;        color: white;    }    QPushButton#orangeButton {        background: #f97316;        color: white;    }    QPushButton#grayButton {        background: #6b7280;        color: white;    }    QPushButton#redButton {        background: #ef4444;        color: white;    }    QPushButton:disabled {        background: #d1d5db;        color: #6b7280;    }    /* ===================== TAB WIDGET (GRİ ŞERİT YOK) ===================== */    QTabWidget {        background: transparent;    }    QTabWidget::pane {        border: none;        background: transparent;        margin: 0px;        padding: 0px;    }    QTabBar {        background: transparent;    }    QTabBar::tab {        background: # #ffffff;        border: 1px solid #e5e7eb;        padding: 6px 12px;        border-top-left-radius: 8px;        border-top-right-radius: 8px;        margin-right: 4px;        font-weight: 700;        font-size: 11px;    }    QTabBar::tab:selected {        background: #ffffff;        border-bottom: none;    }    /* ===================== TOOLBAR (MATPLOTLIB) ===================== */    /* MATPLOTLIB TOOLBAR ARKASI */    QToolBar {        background: #ffffff;        border: none;    }        QToolBar QToolButton {        background: transparent;    }    /* ===================== SCROLL ===================== */    QScrollBar:vertical {        width: 6px;        background: transparent;    }    QScrollBar::handle:vertical {        background: #d1d5db;        border-radius: 3px;    }    QScrollBar::handle:vertical:hover {        background: #9ca3af;    }    QScrollBar::add-line,    QScrollBar::sub-line {        height: 0px;    }    """